create table works
(
    id                   int auto_increment
        primary key,
    user_id              int                                                         null,
    work_id              int                                                         null comment '区块链作品id',
    title                varchar(100)                                                not null,
    description          text                                                        null,
    img_url              varchar(100)                                                null comment '图片路径',
    hash                 varchar(64)                                                 null,
    blockchain_hash      varchar(100)                                                null,
    status               tinyint    default 0                                        null comment '0 - pending, 1 - approved',
    digital_copyright_id varchar(100)                                                null,
    transaction_hash     varchar(100)                                                null,
    category             enum ('艺术类', '虚拟场景类', '游戏类', '风景类', '其他类') null comment '作品种类',
    is_on_auction        tinyint(1) default 0                                        not null,
    created_at           timestamp  default CURRENT_TIMESTAMP                        null,
    constraint blockchain_hash
        unique (blockchain_hash),
    constraint digital_copyright_id
        unique (digital_copyright_id),
    constraint hash
        unique (hash),
    constraint works_pk
        unique (work_id),
    constraint works_ibfk_1
        foreign key (user_id) references users (id)
);

create index user_id
    on works (user_id);

INSERT INTO digitalcopyright.works (id, user_id, work_id, title, description, img_url, hash, blockchain_hash, status, digital_copyright_id, transaction_hash, category, is_on_auction, created_at) VALUES (47, 1, 1, '春日写生', '春日阳光洒满大地，柳树垂荫，桃花盛开，色彩斑斓。微风拂过，湖水泛起涟漪，远处山峦青翠，近处草地青绿，鸟鸣声声，充满生机与活力，仿佛整个世界都在复苏。', '1735542544744_春日写生.jpg', '486299b9565eda0c5f820859228bffa3a54c94b1fffb58f049e93bb23a11dc1a', '486299b9565eda0c5f820859228bffa3a54c94b1fffb58f049e93bb23a11dc1a', 1, 'Copr202420245585318', '0xaed67a55f336e5d53ecefe268a8e9787afbbdc84c13c75c4b278175b3fb0ea47', '艺术类', 1, '2024-12-30 15:09:05');
INSERT INTO digitalcopyright.works (id, user_id, work_id, title, description, img_url, hash, blockchain_hash, status, digital_copyright_id, transaction_hash, category, is_on_auction, created_at) VALUES (48, 1, 2, '春生', '春生，万物复苏，冰雪消融，草木吐绿。柳枝轻摇，花朵竞放，空气中弥漫着泥土与花香的气息。阳光温暖，微风拂面，大地焕发出勃勃生机，仿佛一切都在等待新的开始。', '1735542603016_春生.jpg', '8531066ea9772ac32ada889ba18d25bec45fd43fcd10830900c3655ef3531b00', '8531066ea9772ac32ada889ba18d25bec45fd43fcd10830900c3655ef3531b00', 1, 'Copr202420245543627', '0x30ba6affb1a970d32fe0eb10a765f8ff3c6e3123b7aac2822673778f3ba8ff82', '艺术类', 1, '2024-12-30 15:10:04');
INSERT INTO digitalcopyright.works (id, user_id, work_id, title, description, img_url, hash, blockchain_hash, status, digital_copyright_id, transaction_hash, category, is_on_auction, created_at) VALUES (49, 1, 3, '风景', '春日风景，青山如黛，溪水潺潺，绿树成荫。柳条轻舞，花开缤纷，草地青青，空气清新。远处，山峦叠翠，近处，湖水泛起微波，鸟儿飞翔，整个世界宁静而美丽，生机盎然。', '1735542666571_风景.jpg', '05fb314ceb1f9e9d9d0e506a97ca4d57896ef26e116d364ffe6868bc2a8dd5ee', '05fb314ceb1f9e9d9d0e506a97ca4d57896ef26e116d364ffe6868bc2a8dd5ee', 1, 'Copr202420245562908', '0x8d4729171c8e2fccd0d60cdaca35199663ca75d8ff810bbfc77121147f613140', '艺术类', 1, '2024-12-30 15:11:07');
INSERT INTO digitalcopyright.works (id, user_id, work_id, title, description, img_url, hash, blockchain_hash, status, digital_copyright_id, transaction_hash, category, is_on_auction, created_at) VALUES (50, 1, 4, '蘑菇屋', '蘑菇屋，圆顶如伞，温暖可爱，仿佛童话世界的栖息地。屋顶被藤蔓缠绕，四周绿意盎然，草地上点缀着野花。小木窗透出温柔的灯光，屋前有小径蜿蜒通向森林，充满了宁静与幻想。', '1735542949410_蘑菇屋.jpg', 'bc95ab123d1f7b4f755082ca915e2b9c2b03248e9ee7fceb78b98f527c84a428', 'bc95ab123d1f7b4f755082ca915e2b9c2b03248e9ee7fceb78b98f527c84a428', 1, 'Copr202420245635256', '0x586f112a83e060c7b88700fb208926c92c006cda6120c9f399faa67a47b184b3', '艺术类', 1, '2024-12-30 15:15:50');
INSERT INTO digitalcopyright.works (id, user_id, work_id, title, description, img_url, hash, blockchain_hash, status, digital_copyright_id, transaction_hash, category, is_on_auction, created_at) VALUES (51, 1, 5, '自行车女陔', '“她骑着自行车，轻风拂过脸庞，黑发随风飘扬。车轮在路上转动，带起一阵阵清新的气息。穿着简单的裙子，脚踏轻快，她仿佛是城市中最自由的存在，微笑着穿行在人群中，身后留下一条自由的轨迹。”', '1735543012488_自行车女孩.jpg', 'bfa873dc818b895d0ae6d274a2bd1f649a9e0a66bbacd7438c4593ec6e2891fd', 'bfa873dc818b895d0ae6d274a2bd1f649a9e0a66bbacd7438c4593ec6e2891fd', 1, 'Copr202420245615877', '0x691c2f62f7028e75f91859eb66859d7e674c4a166556e5996c565d966da4557e', '艺术类', 1, '2024-12-30 15:16:53');
INSERT INTO digitalcopyright.works (id, user_id, work_id, title, description, img_url, hash, blockchain_hash, status, digital_copyright_id, transaction_hash, category, is_on_auction, created_at) VALUES (52, 6, 6, '风鹤', '“风鹤翱翔于蓝天，羽翼展开，轻盈地穿梭于云层之间。微风吹拂，鹤影在空中划出优雅的弧线，仿佛与天地融为一体。它们在风中舞动，自由而宁静，仿佛在诉说着大自然的无声诗篇。”', '1735543137463_风鹤.jpg', 'f368e66b61c1963b32e51c53e2bf3afbf7e20044e58a0c2c547eb5ef7434d431', 'f368e66b61c1963b32e51c53e2bf3afbf7e20044e58a0c2c547eb5ef7434d431', 1, 'Copr202420245614931', '0x9baa9c64d92d6a5af4f1bb9ba584327c78e4ac1b60a761046e3a2df276dfa093', '艺术类', 1, '2024-12-30 15:18:58');
INSERT INTO digitalcopyright.works (id, user_id, work_id, title, description, img_url, hash, blockchain_hash, status, digital_copyright_id, transaction_hash, category, is_on_auction, created_at) VALUES (53, 6, 7, '风停在窗边，嘱咐你要热爱这个世界', '“风停在窗边，轻轻拂过玻璃，仿佛在低语。它带着温暖的气息，嘱咐你要热爱这个世界——无论是明媚的阳光，还是细雨的滋润；无论是欢笑的瞬间，还是沉默的孤独。风的声音，柔和却坚定，仿佛在提醒你，珍惜每一份平凡的美好。”', '1735543172965_风停在窗边，嘱咐你要热爱这个世界.jpg', '2f31ad296abce7edca2d60f0187504f58c4a136ac71bd8343ecc1a15bcdbafe8', '2f31ad296abce7edca2d60f0187504f58c4a136ac71bd8343ecc1a15bcdbafe8', 1, 'Copr202420245604794', '0x640637975bdbda05e342bfbb1f8814cffd51b3ceedbf0b93bab16bc36919ca89', '艺术类', 1, '2024-12-30 15:19:33');
INSERT INTO digitalcopyright.works (id, user_id, work_id, title, description, img_url, hash, blockchain_hash, status, digital_copyright_id, transaction_hash, category, is_on_auction, created_at) VALUES (54, 6, 8, '花下乘凉梦', '“在盛开的花树下，微风拂过，花香弥漫，凉意沁人心脾。你倚靠在树旁，静静地闭上眼，任思绪随着花瓣飘落，进入一个柔软的梦境。梦中，一切都是温暖的，光影斑驳，鸟语花香，仿佛时间都停滞，只剩下这一刻的宁静与美好。”', '1735543223754_花下乘凉梦.jpg', 'c6af71f9619c2d83a916d840ec13572e7d01ec712339f3667c063063c338a947', 'c6af71f9619c2d83a916d840ec13572e7d01ec712339f3667c063063c338a947', 1, 'Copr202420245604246', '0xe5bec10001eea099dda756a33cfa31ee7f13da7e9b8d2de1d3401be66728fd4f', '艺术类', 1, '2024-12-30 15:20:24');
INSERT INTO digitalcopyright.works (id, user_id, work_id, title, description, img_url, hash, blockchain_hash, status, digital_copyright_id, transaction_hash, category, is_on_auction, created_at) VALUES (55, 6, 9, '平湖烟雨-凉', '“平湖如镜，烟雨朦胧，远山隐约，水天一色。细雨轻洒，湖面泛起层层涟漪，空气中弥漫着清新的凉意。微风拂过，带着一丝丝湿润，仿佛能洗净尘世的喧嚣与浮躁。湖边的柳树低垂，枝条轻摆，静谧与凉意交织成一幅宁静的画卷。”', '1735543278344_平湖烟雨-凉.png', '4cb1fc24e7941be7928dbdbd15e759217d15b53892cfdecdb464bc06cb346dd4', '4cb1fc24e7941be7928dbdbd15e759217d15b53892cfdecdb464bc06cb346dd4', 1, 'Copr202420245544323', '0x4ffbd45f2ac6dd41f30dffb1f41d3f675459091c7f61814995bc84ac6a445d98', '艺术类', 1, '2024-12-30 15:21:19');
INSERT INTO digitalcopyright.works (id, user_id, work_id, title, description, img_url, hash, blockchain_hash, status, digital_copyright_id, transaction_hash, category, is_on_auction, created_at) VALUES (56, 6, 10, '银月森林', '“银月挂在夜空，洒下清冷的光辉，照亮了静谧的森林。树影斑驳，银色的光芒透过枝叶洒在地上，像是细碎的星辰。微风吹过，叶片沙沙作响，仿佛在低语。森林中的一切在月光下显得更加神秘而宁静，仿佛进入了一个隐秘的世界，只有夜晚与银月见证这片古老森林的故事。”', '1735543307657_银月森林.jpg', '6797e0171973256fa1fd534304a0649273bce140b376898e481b48118543b114', '6797e0171973256fa1fd534304a0649273bce140b376898e481b48118543b114', 1, 'Copr202420245563443', '0xcad0047f8e9918da8132f936e48a4ede54afe901190cc1db87bd8be31f876584', '艺术类', 1, '2024-12-30 15:21:48');
INSERT INTO digitalcopyright.works (id, user_id, work_id, title, description, img_url, hash, blockchain_hash, status, digital_copyright_id, transaction_hash, category, is_on_auction, created_at) VALUES (57, 5, 11, '柿柿如意', '“秋风轻拂，柿树上挂满了沉甸甸的果实，橙红的柿子在阳光下闪烁着温暖的光芒。枝头上的每一颗柿子都象征着丰收与好运，似乎在诉说着‘柿柿如意’的祝愿。树下，微风轻扬，空气中弥漫着熟果的甜香，仿佛一切都在这丰盈的季节里找到了圆满与安宁。”', '1735543533427_柿柿如意.jpg', '3fcc7ed7a883a38f6649406de03ce42e3bb2315d9cb208eecc0e5f08bde7e66a', '3fcc7ed7a883a38f6649406de03ce42e3bb2315d9cb208eecc0e5f08bde7e66a', 1, 'Copr202420245553043', '0xc5e87cc572023efaf9311e12450957e667c0a1e2a582ca5d2658920f4698e2b5', '艺术类', 1, '2024-12-30 15:25:33');
INSERT INTO digitalcopyright.works (id, user_id, work_id, title, description, img_url, hash, blockchain_hash, status, digital_copyright_id, transaction_hash, category, is_on_auction, created_at) VALUES (58, 5, 12, '劳动', '“黎明的第一缕阳光洒在田野上，农人弯腰耕耘，汗水浸湿了衣襟，却换来丰收的希望。工厂的机器轰鸣，工人们忙碌的身影在生产线上来回穿梭，他们的每一滴汗水都是社会发展的动力。劳动，点滴积累，成就了生活的美好与梦想的实现。”', '1735543569477_劳动.jpg', '41aba2fdf1144cb2c05dc04a4a915536eb88ca74b81b22d13d1f39cac5628b86', '41aba2fdf1144cb2c05dc04a4a915536eb88ca74b81b22d13d1f39cac5628b86', 1, 'Copr202420245595520', '0x589bf16c221b047d104ba65a7d24b392b4e89c982a0b587674134f6ccb187706', '艺术类', 1, '2024-12-30 15:26:10');
INSERT INTO digitalcopyright.works (id, user_id, work_id, title, description, img_url, hash, blockchain_hash, status, digital_copyright_id, transaction_hash, category, is_on_auction, created_at) VALUES (59, 5, 13, '秋风萧瑟', '“秋风萧瑟，吹过空旷的田野，枯叶在风中飘零，仿佛诉说着季节的变迁。远处的山脉被薄雾笼罩，天空渐渐沉寂，万物在风中微微颤动。秋日的阳光透过云层，洒在大地上，映照出一片金黄与寂寥，仿佛是岁月在静静流淌，带走了热烈的夏日，也带来了深沉的思考。”', '1735543703552_秋风萧瑟.jpg', '70683a7812dc334c5ea250bb87e2d414f21b76f36579d5a03487f9d3592824a3', '70683a7812dc334c5ea250bb87e2d414f21b76f36579d5a03487f9d3592824a3', 1, 'Copr202420245610916', '0x8e5f1ca90e0127de30756d04b4c053c851f59126a3fe43f51877ab8cf4c42dd5', '艺术类', 1, '2024-12-30 15:28:24');
INSERT INTO digitalcopyright.works (id, user_id, work_id, title, description, img_url, hash, blockchain_hash, status, digital_copyright_id, transaction_hash, category, is_on_auction, created_at) VALUES (60, 5, 14, '云光轻语', '洁白的云朵像柔软的棉絮，堆叠在湛蓝的天空之下。阳光从云间洒下，带着金色与星点般的光辉，仿佛雨滴和星光交织而成', '1735543855302_1734402948702_F5CBCDF5AD21B60ECC8C45AA83BBEC6E.jpg', 'c8c0d501fa1529745c25fa2389c564505342b8acde0dde6550c41c7a2a01bed3', 'c8c0d501fa1529745c25fa2389c564505342b8acde0dde6550c41c7a2a01bed3', 1, 'Copr202420245604446', '0x6a0cfef41a8884f2570fef8f63b6797ccde457247b5f30f9932d3f249243e5b8', '艺术类', 1, '2024-12-30 15:30:56');
INSERT INTO digitalcopyright.works (id, user_id, work_id, title, description, img_url, hash, blockchain_hash, status, digital_copyright_id, transaction_hash, category, is_on_auction, created_at) VALUES (61, 5, 15, '月伴长夜', '面中，一片寂静的夜色笼罩着大地，天幕如同旋转的星云，流动的笔触为夜空增添了无限的深邃感。一列火车驶过远方的高架桥，车窗透出的金色灯光划破了夜晚的宁静，带着流动的生命气息。画面前景中，一个女孩站在水边，手握一弯新月，仿佛在思索或等待，月亮的柔光洒在她身边，营造出一种神秘而温柔的氛围。', '1735543903489_1734402704235_7DA41578BEA948C10354930BA3F6928F.jpg', 'e2f197463ed35765d30c74294d53065bd65468a5e63135a1eaf98ae5e495d7d4', 'e2f197463ed35765d30c74294d53065bd65468a5e63135a1eaf98ae5e495d7d4', 1, 'Copr202420245565732', '0xf92a06417d24f9cd3b5e7b2dd509c9ec32ac3163f8c3953677fe71122fc92bda', '艺术类', 1, '2024-12-30 15:31:44');
INSERT INTO digitalcopyright.works (id, user_id, work_id, title, description, img_url, hash, blockchain_hash, status, digital_copyright_id, transaction_hash, category, is_on_auction, created_at) VALUES (62, 2, 16, '逐光之影', '一只银白色的狼轻盈地奔跑在波光粼粼的水面上，溅起细小的涟漪和水花，仿佛它与自然融为一体。远处的群山在黄昏与夜幕的交替中显得静谧而深邃，太阳的余晖与新月的微光交织，为整个画面增添了一种时间流动的张力。', '1735544965166_1734402746738_3DF23DBD9AFD1B896BB2BADB99FFD7DE.jpg', '219fcec839c953354bf4644ecdd89c9cca1a471569276a744fff744856d7ca88', '219fcec839c953354bf4644ecdd89c9cca1a471569276a744fff744856d7ca88', 1, 'Copr202420245559384', '0xe05731f73d8519ea9d8acb895d3068019c22f79c5782d02fb979e73a4056d26d', '艺术类', 1, '2024-12-30 15:49:25');
INSERT INTO digitalcopyright.works (id, user_id, work_id, title, description, img_url, hash, blockchain_hash, status, digital_copyright_id, transaction_hash, category, is_on_auction, created_at) VALUES (63, 2, 17, '雨中荷韵', '远处的背景以柔和的绿黄色交织，隐隐可见山水的轮廓，模糊而空灵。画面下方，一朵粉红色的荷花静静地绽放在水面上，成为整幅画的焦点，为这片朦胧的雨景增添了一抹鲜艳的生命力。', '1735545017090_1734402829360_B6F261C84CE4AD5D7FAC33A603548E7F.jpg', '1327d33f01a63a7e0ee63e9c6d3ac88eefccd557655a808ef3eedc42362a9509', '1327d33f01a63a7e0ee63e9c6d3ac88eefccd557655a808ef3eedc42362a9509', 1, 'Copr202420245605516', '0xf23e8e94df6faba26f2851de45c234ae80c97c713fb49a964f74eb0dc7dec5b1', '艺术类', 1, '2024-12-30 15:50:17');
INSERT INTO digitalcopyright.works (id, user_id, work_id, title, description, img_url, hash, blockchain_hash, status, digital_copyright_id, transaction_hash, category, is_on_auction, created_at) VALUES (64, 2, 18, '晨曦云梦', '太阳位于地平线附近，散发出温暖的光辉，将天空渲染成粉色、橙色和淡黄色的渐变色彩。周围的云朵如丝绸般轻盈，流动的笔触将天光和云层融合在一起，增添了一种动态感。', '1735545058831_1734403008239_CBB277CE90BBF78D7B30D818E44C3958.jpg', '6c8e87a348ff47912562f4a06cf5f8a5a8434e1469dbbe4a9102ef9b7e1c5ad3', '6c8e87a348ff47912562f4a06cf5f8a5a8434e1469dbbe4a9102ef9b7e1c5ad3', 0, null, '0xd04a7474c6537a08b4d3e633d2f9918bc77ce0e96ec59ae134c9608c75b592a5', '艺术类', 0, '2024-12-30 15:50:59');
INSERT INTO digitalcopyright.works (id, user_id, work_id, title, description, img_url, hash, blockchain_hash, status, digital_copyright_id, transaction_hash, category, is_on_auction, created_at) VALUES (65, 2, 19, '烟波人远', '一位渔人在小舟上垂钓，身影静默，与这片湖光山色融为一体，给人一种人与自然和谐相处的美感。渔舟与湖面之间的互动让画面生动而充满生活气息。', '1735545111327_1734402789188_34167689AF9726CE207118A798D11D8B.jpg', 'ee2a8ff8be04fb00ad7a9786d370c39f3f6e019ec491b7a9e64caf78284da94a', 'ee2a8ff8be04fb00ad7a9786d370c39f3f6e019ec491b7a9e64caf78284da94a', 0, null, '0xa724e04edf6b45d2554c639e95c01a09090f822067d8f5ed69c21c53340157bf', '艺术类', 0, '2024-12-30 15:51:52');
